    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Hello ModifyUserController!{% endblock %}


{% block body %}

    {% block content %}

        <div class=\"container d-flex justify-content-center my-5 \">
            <div class=\"row w-50\">
                <div class=\"col-lg-12 grid-margin stretch-card\">
                    <div class=\"card\">
                        <div class=\"card-body pt-3\">
                            <h4 class=\"card-title\">Changer Votre Mot de passe</h4>

                            {{ form_start(form) }}
                            <div class=\"form-group\">

                                {{ form_label(form.email,\"Email\") }}
                                {{ form_errors(form.email)  }}
                                {{ form_widget(form.email , {'attr':{'class':'form-control p_input'}}) }}
                            </div>
                            <div class=\"form-group\">
                                {{ form_label(form.nom,\"Nom\") }}
                                {{ form_errors(form.nom)  }}
                                {{ form_widget(form.nom , {'attr':{'class':'form-control p_input'}}) }}
                            </div>
                            <div class=\"form-group\">
                                {{ form_label(form.prenom,\"Prénom\") }}
                                {{ form_errors(form.prenom)  }}
                                {{ form_widget(form.prenom , {'attr':{'class':'form-control p_input'}}) }}
                            </div>

                            <div class=\"form-group\">

                                {{ form_label(form.old_password,\"Ancien Mot de passe\") }}
                                {{ form_errors(form.old_password)  }}
                                {{ form_widget(form.old_password , {'attr':{'class':'form-control p_input'}}) }}
                            </div>


                            <div class=\"form-group\">

                                {{ form_label(form.new_password.first,\"Nouveau de mot de passe \") }}
                                {{ form_errors(form.new_password.first)  }}
                                {{ form_widget(form.new_password.first , {'attr':{'class':'form-control p_input'}}) }}
                            </div>
                            <div class=\"form-group\">

                                {{ form_label(form.new_password.second,\"Nouveau de mot de passe \") }}
                                {{ form_errors(form.new_password.second)  }}
                                {{ form_widget(form.new_password.second , {'attr':{'class':'form-control p_input'}}) }}
                            </div>

                            <div class=\"text-center d-flex justify-content-around\">
                                {{ form_row(form.submit, {'attr': {'class' : 'btn btn-warning btn-block enter-btn text-white'}} ) }}
                                <button class=\"btn \">
                                    <a href=\"/user/compte\">Cancel</a>
                                </button>
                            </div>

                            {{ form_end(form) }}
                        </div>
                    </div>
                </div>
            </div>
        </div>

    {% endblock %}
{% endblock %}
", "modify_user/modify-password.html.twig", "C:\\Users\\21695\\Documents\\I-Plus-Plus\\templates\\modify_user\\modify-password.html.twig");
